$created = 0
foreach ($issue in $issues) {
    try {
        $result = gh issue create --repo $repo --title $issue.title --body $issue.body --label "enhancement" 2>&1
        $created++
        Write-Host "[$created/$($issues.Count)] Created: $($issue.title)"
    } catch {
        Write-Host "FAILED: $($issue.title) - $_"
    }
}

Write-Host "`nDone! Created $created / $($issues.Count) issues."
